# ------------------- OWNER & SUDO -------------------
OWNER_ID = 5311223486  # Change to your Telegram user ID
SUDO_FILE = "sudo.json"

# Load sudo list from file or create empty
try:
    with open(SUDO_FILE, "r") as f:
        SUDO_LIST = json.load(f)
except:
    SUDO_LIST = []

def save_sudo():
    with open(SUDO_FILE, "w") as f:
        json.dump(SUDO_LIST, f)

def is_owner(user_id):
    return user_id == OWNER_ID

def is_sudo(user_id):
    return user_id in SUDO_LIST or is_owner(user_id)

# ------------------- SUDO COMMANDS -------------------
@bot.message_handler(commands=["addsudo"])
def addsudo_cmd(message):
    if not is_owner(message.from_user.id):
        bot.reply_to(message, "тЭМ Only owner can add sudo users")
        return

    if message.reply_to_message:
        user_id = message.reply_to_message.from_user.id
    else:
        bot.reply_to(message, "тЭМ Reply to the user you want to add as sudo")
        return

    if user_id in SUDO_LIST:
        bot.reply_to(message, "тЬЕ User is already sudo")
        return

    SUDO_LIST.append(user_id)
    save_sudo()
    bot.reply_to(message, f"тЬЕ Added user as sudo: {user_id}")

@bot.message_handler(commands=["remsudo"])
def remsudo_cmd(message):
    if not is_owner(message.from_user.id):
        bot.reply_to(message, "тЭМ Only owner can remove sudo users")
        return

    if message.reply_to_message:
        user_id = message.reply_to_message.from_user.id
    else:
        bot.reply_to(message, "тЭМ Reply to the user you want to remove from sudo")
        return

    if user_id not in SUDO_LIST:
        bot.reply_to(message, "тЭМ User is not in sudo list")
        return

    SUDO_LIST.remove(user_id)
    save_sudo()
    bot.reply_to(message, f"тЬЕ Removed user from sudo: {user_id}")

@bot.message_handler(commands=["sudolist"])
def sudolist_cmd(message):
    if not is_owner(message.from_user.id):
        bot.reply_to(message, "тЭМ Only owner can see sudo list")
        return

    if not SUDO_LIST:
        bot.reply_to(message, "тЪая╕П Sudo list is empty")
    else:
        text = "ЁЯСС Sudo Users:\n" + "\n".join([str(u) for u in SUDO_LIST])
        bot.reply_to(message, text)

